/* ---WIDGETS--- */
.Widgets{
background-color: rgb(0, 0, 0, .4);
}

/* ----TIME---- */
.Time h3 {
font-size:1.5rem;
margin: .7rem 0 1.2rem 0;
}
.Time hr {
border: 0;
margin: 0;
}

/* ----LINKS---- */
.Links {
padding-top: 1rem;
border-top: 1px solid white;
}
.Links a {
font-size: 1.2rem;
background: transparent;
padding: .5rem;
border-radius: 0.5rem;
border:1px solid white;
transition: .3s;
}
.Links a:hover {
transform: translateY(-2px);
}
.Links a:hover .LinkText {
text-decoration: none;
}

/* ----WORKHOURS---- */
.WorkHours{
font-size:.7rem;
}

/* ----WEATHER---- */
.Weather {
position: absolute;
top: .7rem;
left: 0;
display:flex;
flex-direction:column;
justify-content:center;
align-items: end;
width: 100%;
font-size: 1.2rem;
padding-right: 1rem;
}
.Weather .details, .Weather .details dl {
display: flex;
flex-direction: row;
justify-content: center;
}
.Weather .details dl{
margin: 0 0 0 1rem;
}
.Weather .details dl dd {
margin-left: .2rem;
}

/* ----GENERAL---- */
.Dashboard .container {
display: flex;
justify-content: center;
align-items: center;
}
.Slot {
position:initial;
}
.Slot.middleCentre {
transform: none;
}
